mapper.select:
SELECT gb.* ,p.* FROM goods_database gb LEFT JOIN purchase p ON gb.goods_database_code = p.goods_database_code WHERE p.goods_database_code IS NULL
AND gb.${select} LIKE CONCAT('%',#{searchInput},'%')
AND gb.goods_database_date between #{beginDate} and NOW()
AND gb.goods_database_date '2000-01-01' and #{endDate}
AND gb.goods_database_date between #{beginDate} and #{endDate}

SELECT gb.* ,p.* FROM goods_database gb , purchase p WHERE gb.goods_database_code = p.goods_database_code
AND gb.${select} LIKE CONCAT('%',#{searchInput},'%')
AND gb.goods_database_date between #{beginDate} and NOW()
AND gb.goods_database_date '2000-01-01' and #{endDate}
AND gb.goods_database_date between #{beginDate} and #{endDate}

SELECT goods_database_code AS goodsDbCode, gdb.member_id AS memberId, goods_database_name AS goodsDbName, goods_database_category AS goodsDbCategory, goods_database_number AS goodsDbNumber, goods_database_color AS goodsDbColor, goods_database_weight AS goodsDbWeight, goods_database_speed AS goodsDbSpeed, goods_database_size AS goodsDbSize, goods_database_nation AS goodsDbNation, goods_database_load AS goodsDbLoad, goods_database_distance AS goodsDbDistance, goods_database_battery AS goodsDbBattery, goods_database_time AS goodsDbTime, goods_database_made AS goodsDbMade, goods_database_description AS goodsDbDescription, goods_database_image AS goodsDbImage, gdb.goods_database_date AS goodsDbDate
FROM goods_database gdb INNER JOIN member m ON gdb.member_id = m.member_id
WHERE ${select} Like CONCAT('%',#{searchInput},'%')
AND gdb.goods_database_date between #{beginDate} and NOW()
AND gdb.goods_database_date between '2000-01-01' and #{endDate}
AND gdb.goods_database_date between #{beginDate} and #{endDate}

mapper.delete:
DELETE
gdb
FROM
goods_database gdb INNER JOIN member m
WHERE
gdb.goods_database_code =
#{goodsDbCode}
AND m.member_id = #{memberId}
AND m.member_pw =
#{memberPw}
